package sohal.lld.jobqueue.config;import org.apache.kafka.clients.admin.NewTopic;import org.springframework.beans.factory.annotation.Value;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.kafka.config.TopicBuilder;@Configurationpublic class KafkaTopicConfig {    @Value("${jobs.topic}")    private String jobsTopic;    @Bean    public NewTopic jobsDispatchTopic() {        return TopicBuilder.name(jobsTopic)                .partitions(3)                .replicas(1)                .build();    }}